font.SCRIPT:
 

var ostype=new OSType();
if (ostype.isMac){
        oVDiv=document.getElementById("pcinstall").style.display="none";
        oVDiv=document.getElementById("macinstall").style.display="block";
        oVDiv=document.getElementById("pcserviceinstall").style.display="none";
}
else if (ostype.winvista) {
        oVDiv=document.getElementById("pcinstall").style.display="none";
        oVDiv=document.getElementById("macinstall").style.display="none";
        oVDiv=document.getElementById("pcserviceinstall").style.display="block";
}
else {
        oVDiv=document.getElementById("pcinstall").style.display="block";
        oVDiv=document.getElementById("macinstall").style.display="none";
        oVDiv=document.getElementById("pcserviceinstall").style.display="none";
}
